        if (compare_case == "o")  return ratio_s_o[np-lowest_np_s];
        if (compare_case == "ob") return ratio_s_ob[np-lowest_np_s];

        if (compare_case == "woallx_for_wx")  return ratio_s_woallx_for_wx[np-lowest_np_s];
